import {useEffect, useRef, useState} from 'react';

const SimpleInput = (props) => {

  //ref
  const nameInputRef = useRef();

  //state
  const [enteredName, setEnteredName] = useState('');

  //providing validation feedback
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(true);

  useEffect(() => {
    if (enteredNameIsValid) {
      console.log("Name input valid");
    }
  }, [entered])

  //state
  const nameInputChangeHandler = event => {
    setEnteredName(event.target.value);
  };

  const formSubmissionHandler = event => {
    event.preventDefault();

    //basic validation
    if (enteredName.trim() === '') {
      setEnteredNameIsValid(false);
      return;
    }
    setEnteredNameIsValid(true);

    console.log("Use State method", enteredName);

    const enteredValue = nameInputRef.current.value;
    console.log("Use Ref method", enteredValue);

    //Setting input back to blank
    nameInputRef.current.value = ''; //bad because your directly manipulating the dom
    setEnteredName('');

  };

  const nameInputClasses = enteredNameIsValid ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input 
        ref={nameInputRef} 
        type='text' id='name' 
        onChange={nameInputChangeHandler}
        value={enteredName}
        />
        {!enteredNameIsValid && <p className="error-text">Name must not be empty.</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;

/*
There are two ways we can fetch the entered value.
1. We can listen to every keystroke and store the value in some state variable.
2. We can use useRef to fetch the input once the user is done typing in a value.

*/
