import {useEffect, useRef, useState} from 'react';

const SimpleInput = (props) => {

  //ref
  // const nameInputRef = useRef();

  //state
  const [enteredName, setEnteredName] = useState('');

  //providing validation feedback
  // const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);

  //handling the was touched state
  const [enteredNameTouched, setEnterednameTouched] = useState(false);

  //if one input is invalid the hwole form is invalid
  const [formIsValid, setFormIsValid] = useState(false);



  // useEffect(() => {
  //   if (enteredNameIsValid) {
  //     console.log("Name input valid");
  //   }
  // }, [enteredNameIsValid]);

  const enteredNameIsValid = enteredName.trim() !== '';
  const nameInputIsInvalid = !enteredNameIsValid && enteredNameTouched;

  useEffect(() => {
    if (enteredNameIsValid) {
      setFormIsValid(true);
    } else {
      setFormIsValid(false);
    }
  }, [enteredNameIsValid]);

  //state
  const nameInputChangeHandler = event => {
    setEnteredName(event.target.value);

    // if (event.target.value.trim() !== '') {
    //   setEnteredNameIsValid(true);
    // }
  };

  const nameInputBlurHandler =  event => {
    setEnterednameTouched(true);

    // if (enteredName.trim() === '') {
    //   setEnteredNameIsValid(false);
    // }

  };

  const formSubmissionHandler = event => {
    event.preventDefault();

    //seeing is user has already touched it
    setEnterednameTouched(true);

    //basic validation
    if (!enteredNameIsValid) {
      // setEnteredNameIsValid(false);
      return;
    }
    // setEnteredNameIsValid(true);

    console.log("Use State method", enteredName);

    // const enteredValue = nameInputRef.current.value;
    // console.log("Use Ref method", enteredValue);

    //Setting input back to blank
    // nameInputRef.current.value = ''; //bad because your directly manipulating the dom
    setEnteredName('');
    setEnterednameTouched(false);
  };

  const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control';

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input 
        // ref={nameInputRef} 
        type='text' id='name' 
        onChange={nameInputChangeHandler}
        onBlur={nameInputBlurHandler}
        value={enteredName}
        />
        {nameInputIsInvalid && <p className="error-text">Name must not be empty.</p>}
      </div>
      <div className="form-actions">
        <button disabled={!form}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;

/*
There are two ways we can fetch the entered value.
1. We can listen to every keystroke and store the value in some state variable.
2. We can use useRef to fetch the input once the user is done typing in a value.

onBlur() - Used when something looses focus.
*/
