import {useRef, useState} from 'react';

const SimpleInput = (props) => {

  //ref
  const nameInputRef = useRef();

  //state
  const [enteredName, setEnteredName] = useState('');

  //state
  const nameInputChangeHandler = event => {
    setEnteredName(event.target.value);
  };

  const formSubmissionHandler = event => {
    event.preventDefault();

    //


    console.log("Use State method", enteredName);

    const enteredValue = nameInputRef.current.value;
    console.log("Use Ref method", enteredValue);

    //Setting input back to blank
    nameInputRef.current.value = ''; //bad because your directly manipulating the dom
    setEnteredName('');

  };

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className='form-control'>
        <label htmlFor='name'>Your Name</label>
        <input 
        ref={nameInputRef} 
        type='text' id='name' 
        onChange={nameInputChangeHandler}
        value={enteredName}
        />
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;

/*
There are two ways we can fetch the entered value.
1. We can listen to every keystroke and store the value in some state variable.
2. We can use useRef to fetch the input once the user is done typing in a value.

*/
